  // function Titulo({tit, subTitulo}) -> esta es la mejor alternativa porque permite eliminar toda la otra linea

const {tit} = propiedades // porque es un objeto 
const stylo={backgroundColor: 'blue', color: 'red'} //estilos multiples

  return (
  <div>
  <h1 style={stylo} onClick={()=>console.log('me clickeaste')}>Hello World!</h1>
  <h1>{propiedades.tit}</h1> 

  </div>
  )
}

